examples.source:
import { TableSheet } from '@antv/s2';
import '@antv/s2/dist/s2.min.css';

fetch(
  'https://gw.alipayobjects.com/os/bmw-prod/d62448ea-1f58-4498-8f76-b025dd53e570.json',
)
  .then((res) => res.json())
  .then((data) => {
    const container = document.getElementById('container');
    const s2DataConfig = {
      fields: {
        columns: ['province', 'city', 'type', 'price', 'cost'],
      },
      meta: [
        {
          field: 'province',
          name: '省份',
        },
        {
          field: 'city',
          name: '城市',
        },
        {
          field: 'type',
          name: '商品类别',
        },
        {
          field: 'price',
          name: '价格',
        },
        {
          field: 'cost',
          name: '成本',
        },
      ],
      data,
    };

    const s2Options = {
      width: 600,
      height: 480,
    };

    const BORDER_COLOR = 'rgb(39, 44, 65)';
    const BACK_COLOR = 'rgb(67, 72, 91)';
    const HEADER_BACK_COLOR = '#353c59';
    const CELL_ACTIVE_BACK_COLOR = '#434c6c';

    const customTheme = {
      background: {
        color: HEADER_BACK_COLOR,
      },
      cornerCell: {
        cell: {
          horizontalBorderColor: BORDER_COLOR,
          verticalBorderColor: BORDER_COLOR,
          padding: {
            top: 12,
            right: 8,
            bottom: 12,
            left: 8,
          },
          backgroundColor: HEADER_BACK_COLOR,
        },
        text: {
          fill: '#fff',
        },
        bolderText: {
          fill: '#fff',
          opacity: 0.4,
        },
      },
      splitLine: {
        horizontalBorderColor: BORDER_COLOR,
        horizontalBorderColorOpacity: 1,
        horizontalBorderWidth: 2,
        verticalBorderColor: BORDER_COLOR,
        verticalBorderColorOpacity: 1,
        verticalBorderWidth: 2,
        showRightShadow: true,
        shadowWidth: 10,
        shadowColors: {
          left: 'rgba(0,0,0,0.1)',
          right: 'rgba(0,0,0,0)',
        },
      },
      rowCell: {
        text: {
          fill: '#fff',
          opacity: 0.4,
          fontSize: 15,
          fontWeight: 'bold',
        },
        cell: {
          backgroundColor: HEADER_BACK_COLOR,
          padding: {
            top: 12,
            right: 8,
            bottom: 12,
            left: 8,
          },
          interactionState: {
            hover: {
              backgroundColor: CELL_ACTIVE_BACK_COLOR,
              backgroundOpacity: 1,
            },
            selected: {
              backgroundColor: 'rgb(63, 69, 97)',
            },
          },
          horizontalBorderColor: BORDER_COLOR,
          verticalBorderColor: BORDER_COLOR,
          verticalBorderWidth: 1,
        },
      },
      colCell: {
        cell: {
          horizontalBorderColor: BORDER_COLOR,
          verticalBorderColor: BORDER_COLOR,
          verticalBorderWidth: 2,
          horizontalBorderWidth: 2,
          padding: {
            top: 12,
            right: 8,
            bottom: 12,
            left: 8,
          },
          backgroundColor: HEADER_BACK_COLOR,
          interactionState: {
            hover: {
              backgroundColor: CELL_ACTIVE_BACK_COLOR,
              backgroundOpacity: 1,
            },
            selected: {
              backgroundColor: 'rgb(63, 69, 97)',
            },
          },
        },
        text: {
          fill: '#fff',
        },
        bolderText: {
          fill: '#fff',
          opacity: 0.4,
        },
      },
      dataCell: {
        icon: {
          size: 14,
          margin: {
            left: 10,
          },
        },
        cell: {
          interactionState: {
            hover: {
              backgroundColor: CELL_ACTIVE_BACK_COLOR,
              backgroundOpacity: 1,
            },
            hoverFocus: {
              backgroundColor: CELL_ACTIVE_BACK_COLOR,
              backgroundOpacity: 1,
              borderColor: 'blue',
            },
            selected: {
              backgroundColor: CELL_ACTIVE_BACK_COLOR,
              backgroundOpacity: 1,
            },
            unselected: {
              backgroundOpacity: 1,
              opacity: 1,
            },
            prepareSelect: {
              borderColor: CELL_ACTIVE_BACK_COLOR,
            },
          },
          horizontalBorderColor: BORDER_COLOR,
          verticalBorderColor: BORDER_COLOR,
          verticalBorderWidth: 2,
          horizontalBorderWidth: 2,
          padding: {
            top: 0,
            right: 8,
            bottom: 2,
            left: 0,
          },
          backgroundColorOpacity: 0.9,
          backgroundColor: BACK_COLOR,
        },
        text: {
          fill: '#fff',
        },
      },
    };

    const s2 = new TableSheet(container, s2DataConfig, s2Options);

    s2.setThemeCfg({ theme: customTheme });

    s2.render();
  });

