        columns: ['type', 'sub_type'],
        values: ['number'],
      },
      meta: res.meta,
      data: res.data,
    };
    const s2Options = {
      width: 600,
      height: 480,
      cornerHeader: (node, s2, headConfig) => {
        return new CustomCornerHeader(node);
