        },
        {
          field: 'city',
          name: '城市',
        },
        {
          field: 'type',
          name: '商品类别',
        },
        {
          field: 'price',
          name: '价格',
        },
        {
          field: 'cost',
          name: '成本',
        },
      ],
      data,
      sortParams: [
        {
          sortFieldId: 'price',
          sortBy: (obj) =>
            canConvertToNumber('price') ? Number(obj.price) : obj.price,
          sortMethod: 'DESC',
        },
      ],
    };

    const s2Options = {
      width: 600,
      height: 480,
    };
    const s2 = new TableSheet(container, s2DataConfig, s2Options);

    s2.render();
  });

