import React from 'react';
import ReactDOM from 'react-dom';
import { SheetComponent } from '@antv/s2-react';
import '@antv/s2-react/dist/style.min.css';

fetch('../data/basic.json')
  .then((res) => res.json())
  .then((data) => {
    const dataCfg = {
      meta: [
        {
          field: 'price',
          name: '价格',
          description: '价格说明。。',
        },
        {
          field: 'province',
          name: '省份',
          description: '省份说明。。',
        },
        {
          field: 'city',
          name: '城市',
          description: '城市说明。。',
        },
        {
          field: 'type',
          name: '类别',
          description: '类别说明。。',
        },
      ],
      fields: {
        rows: ['province', 'city'],
        columns: ['type'],
        values: ['price'],
        valueInCols: true,
      },
      data,
    };

    const s2Options = {
      width: 600,
      height: 480,
      tooltip: {
        showTooltip: true,
      },
    };

    ReactDOM.render(
      <SheetComponent
        sheetType="pivot"
        dataCfg={dataCfg}
        options={s2Options}
      />,
      document.getElementById('container'),
    );
  });

