    const button = document.createElement('button');
    const dataCellTooltip = () => {
      button.innerText = '点击合并单元格';
      button.className = 'merge-cells-button';
      button.onclick = () => s2.interaction.mergeCells();
      return button;
    }; // (按住 Cmd/ Ctrl 多选)

    const mergedCellsTooltip = (mergedCell) => {
