          const viewMeta = getCellData(rowIndex, colIndex);
          if (rowIndex === 2 && colIndex === 0) {
            return {
              ...viewMeta,
              data: {
                province: '浙江省',
                city: '宁波市',
                type: '家具',
                sub_type: '桌子',
