import React from 'react';
import ReactDOM from 'react-dom';
import { SheetComponent } from '@antv/s2-react';
import '@antv/s2-react/dist/style.min.css';

// more Info https://observablehq.com/@pearmini/mosaic-antv-s2

fetch(
  'https://gw.alipayobjects.com/os/bmw-prod/7f6ebbb4-ffeb-4f6c-a763-6faa8c0ccf7a.json',
)
  .then((res) => res.json())
  .then((dataCfg) => {
    const s2Options = {
      width: 700,
      height: 575,
      frozenRowHeader: false,
      conditions: {
        background: [
          {
            field: 'color',
            mapping: (fill) => ({ fill }),
          },
        ],
      },
      interaction: {
        hoverHighlight: false,
        hoverFocus: false,
      },
      style: {
        layoutWidthType: 'compact',
        colCfg: {
          height: 0,
          widthByFieldValue: {
            color: 23,
          },
        },
        cellCfg: {
          height: 23,
        },
      },
    };

    const customTheme = {
      rowCell: {
        cell: {
          backgroundColor: dataCfg.data[0].color,
          horizontalBorderColorOpacity: 0,
          verticalBorderColorOpacity: 0,
        },
      },
      dataCell: {
        text: {
          opacity: 0,
        },
        cell: {
          horizontalBorderColorOpacity: 0,
          verticalBorderColorOpacity: 0,
        },
      },
      splitLine: {
        horizontalBorderColorOpacity: 0,
        verticalBorderColorOpacity: 0,
      },
      background: {
        color: dataCfg.data[0].color,
      },
      scrollBar: {
        size: 0,
      },
    };

    ReactDOM.render(
      <SheetComponent
        dataCfg={dataCfg}
        options={s2Options}
        themeCfg={{ theme: customTheme }}
      />,
      document.getElementById('container'),
    );
  });

